type InlineStyle = { [key: string]: unknown };
type StylesArray<T> = T | ReadonlyArray<StylesArray<T>>;
type Styles = StylesArray<InlineStyle | false | void | null>;
export declare function flattenStyle(...styles: Array<Styles>): {
  [key: string]: any;
};
